import pyfigletimport sysimport socketfrom datetime import datetimeimport winsoundascii_banner = pyfiglet.figlet_format("PORT SCANNER")print(ascii_banner)# Defining a target#if len(sys.argv) == 0:    # translate hostname to IPv4#target = socket.gethostbyname("LAPTOP-AECEFHDJ")target = '127.0.0.1'print("IP address of the localhost is {}".format(target))#else:    #print("Invalid amount of Argument")# Add Bannerprint("-" * 50)print("Scanning Target: " + target)print("Scanning started at:" + str(datetime.now()))print("-" * 50)try:    # will scan ports between 1 to 65,535    #for port in range(1, 65535):    s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)    socket.setdefaulttimeout(1)        # returns an error indicator    result = s.connect_ex((target, 502))    if result == 0:        print("Port {} is open".format(502))    else:        print("port {} is close".format(502))    s.close()except KeyboardInterrupt:    print("\n Exiting Program !!!!")    sys.exit()except socket.gaierror:    print("\n Hostname Could Not Be Resolved !!!!")    sys.exit()except socket.error:    print("\ Server not responding !!!!")    sys.exit()